package day13_stringManipulations;

public class C02_Replace {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String sonuc ="10.000";
		
		sonuc=sonuc.replaceFirst(".", "");
		
			
		if (Integer.valueOf(sonuc)>1000) {
			
			System.out.println("");
		} else {
			
			System.out.println("");
		}
	}

}
